import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './results.css';

const ResultPage = () => {
    const [results, setResults] = useState([]);
    const [events, setEvents] = useState([]);
    const [error, setError] = useState('');
    const [selectedResult, setSelectedResult] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const loggedin = sessionStorage.getItem('loggedIn')==='true';

        if (!loggedin){
            navigate('/login');
        } else {
            fetchAthleteIdAndResults();
        }
    }, [navigate]);

    const fetchAthleteIdAndResults = async () => {
        const username = sessionStorage.getItem('username');
        const userType = sessionStorage.getItem('userType');
        if (username && userType === 'athlete') {
            try {
                const athleteResponse = await axios.get(`http://localhost:8080/api/athletes/${username}`);
                const resultsResponse = await axios.get(`http://localhost:8080/api/results/athlete/${athleteResponse.data.id}`);
                setResults(resultsResponse.data);
                const eventResponse = await axios.get(`http://localhost:8080/api/events/${results.data.eventId}`);
                setEvents(eventResponse.data);
            } catch (error) {
                console.error('Error fetching athlete data or results:', error);
                setError('Failed to fetch athlete data or results. Please try again later.');
            }
        } else {
            setError('User is not an athlete or not logged in.');
        }
    };

    const handleViewResult = async (eventId) => {
        try {
            const response = await axios.get(`http://localhost:8080/api/results/${eventId}`);
            setSelectedResult(response.data);
        } catch (error) {
            console.error('Error fetching result details:', error);
            setError('Failed to fetch result details. Please try again later.');
        }
    };

    const ResultPopup = ({ results, onClose }) => {
        const getPlacementMessage = (result) => {
            if (result <= 3) {
                return `Hooray, you were placed ${result}!`;
            }
            return `You were placed ${result}.`;
        };

        return (
            <div className="result-popup">
                <div className="result-popup-content">
                    <img src={events.image_url} alt={events.title} className="event-image" />
                    <h2>{events.title}</h2>
                    <p><strong>Organizer:</strong> {events.organizer}</p>
                    <p><strong>Date:</strong> {events.date}</p>
                    <p><strong>Time:</strong> {events.time}</p>
                    <p><strong>Location:</strong> {events.location}</p>
                    <p><strong>Your Position:</strong> {results.result}</p>
                    <p>{getPlacementMessage(results.result)}</p>
                    {results.result <= 3 && (
                        <div className="podium">
                            <img src="/images/podium.png" alt="Podium" />
                            <div className="podium-names">
                                <span className="second">{results.secondPlace}</span>
                                <span className="first">{results.firstPlace}</span>
                                <span className="third">{results.thirdPlace}</span>
                            </div>
                        </div>
                    )}
                    <button onClick={onClose}>Close</button>
                </div>
            </div>
        );
    };

    return (
        <div className="results-container">
            <h2>Your Results</h2>
            {error && <p className="error-message">{error}</p>}
            <div className="results-grid">
                {results.map(result => (
                    <div key={result.id} className="result-card">
                        <img src={events.image_url} alt={events.title} className="event-image" />
                        <h3>{result.event.title}</h3>
                        <p><strong>Organizer:</strong> {events.organizer}</p>
                        <p><strong>Date:</strong> {events.date}</p>
                        <p><strong>Location:</strong> {events.location}</p>
                        {results.result === 1 ? (
                            <button onClick={() => handleViewResult(events.id)}>View Result</button>
                        ) : (
                            <p className="not-published">Not published</p>
                        )}
                    </div>
                ))}
            </div>
            {selectedResult && <ResultPopup result={selectedResult} onClose={() => setSelectedResult(null)} />}
        </div>
    );
};

export default ResultPage;
