import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './events.css';

// Import your images directly
import sampleImage from './image/sampleImage.jpg'; // Replace with an actual image

const EventPage = () => {
    const [events, setEvents] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchEvents = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/events');
                setEvents(response.data);
                console.log('Fetched events:', response.data); // Log the fetched events
            } catch (error) {
                console.error('Error fetching events:', error);
                setError('Failed to fetch events. Please try again later.');
            }
        };

        fetchEvents();
    }, []);

    return (
        <div className="event-container">
            <h2>Upcoming Events</h2>
            {error && <p className="error-message">{error}</p>}
            {events.length > 0 ? (
                events.map(event => (
                    <div key={event.id} className="event-box">
                        <img
                            src={require(`./image/${event.image_url.split('/').pop()}`)} // Dynamically require the image
                            alt={event.name}
                            className="event-image"
                        />
                        <div className="event-details">
                            <h3>Organizer: {event.organizer}</h3>
                            <p>Date: {event.date}</p>
                            <p>Time: {event.time}</p>
                            <p>Fee: RS {event.fee}</p>
                            <button className="register-button">Register</button>
                        </div>
                    </div>
                ))
            ) : (
                <p>No events available at the moment.</p>
            )}
        </div>
    );
};

export default EventPage;
