import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './events.css';

const EventPage = () => {
    const [events, setEvents] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchEvents = () => {
            axios.get('http://localhost:8080/api/events')
                .then(response => {
                    // Assuming response.data contains the array of events
                    setEvents(response.data);
                })
                .catch(err => {
                    console.error('Error fetching events:', err);
                    setError('Failed to fetch events. Please try again later.');
                });
        };

        fetchEvents();
    }, []);

    return (
        <div className="event-container">
            {error && <p className="error-message">{error}</p>} {/* Show error message if exists */}
            {events.map(event => (
                <div key={event.id} className="event-box">
                    <img src={event.imageUrl} alt={event.organizer} className="event-image" />
                    <div className="event-details">
                        <h3>Organizer: {event.organizer}</h3>
                        <p>Date: {event.date}</p>
                        <p>Time: {event.time}</p>
                        <p>Fee: ${event.fee}</p>
                        <button className="register-button">Register</button>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default EventPage;
