import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import PasswordStrengthMeter from './PasswordStrengthMeter'; // Ensure this is correctly imported
import './signup.css'; // Use your custom signup styles or adjust the styles here
import showPasswordIcon from '../images/show.png'; // Update paths if necessary
import hidePasswordIcon from '../images/hide.png';

const SignupPage = () => {

    useEffect(() => {
        document.title = 'Sign-up Page';
    }, []);

    // Form state
    const [firstname, setFirstname] = useState('');
    const [lastname, setLastname] = useState('');
    const [middlename, setMiddlename] = useState(''); // Optional
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [userType, setUserType] = useState('athlete'); // Default type
    const [showPassword, setShowPassword] = useState(false); // Password visibility
    const [PasswordStrength, setPasswordStrength] = useState(0); // Track password strength

    // Error and success messages
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');

    // Toggle password visibility
    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    // Validate email format
    const validateEmail = (email) => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(email);
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Reset messages
        setError('');
        setMessage('');

        if (!firstname || !lastname || !username || !email || !password || !confirmPassword) {
            setError('All fields are required!');
        } else if (!validateEmail(email)) {
            setError('Enter a valid email address');
        } else if (password !== confirmPassword) {
            setError('Passwords do not match!');
        } else if (PasswordStrength < 2) {
            setError('Password is too weak! Choose a stronger password.');
        } else {
            try {
                // Send formData to backend
                const formData = { firstname, lastname, middlename, username, email, password, userType };
                const response = await axios.post('http://localhost:8080/api/signup', formData);
                setMessage(response.data); // Backend success message
            } catch (error) {
                if (error.response && error.response.data) {
                    setError(error.response.data); // Backend error message
                } else {
                    setError('Signup failed. Please try again.');
                }
            }
        }
    };

    return (
        <div className="signup-container">
            <form className="signup-form" onSubmit={handleSubmit}>
                <h3 className="form-header">Register</h3>

                <div className="name-fields">
                    <input
                        type="text"
                        placeholder="First Name*"
                        value={firstname}
                        onChange={(e) => setFirstname(e.target.value)}
                        required
                    />
                    <input
                        type="text"
                        placeholder="Last Name*"
                        value={lastname}
                        onChange={(e) => setLastname(e.target.value)}
                        required
                    />
                </div>

                <input
                    type="text"
                    placeholder="Middle Name (Optional)"
                    value={middlename}
                    onChange={(e) => setMiddlename(e.target.value)}
                />

                <input
                    type="text"
                    placeholder="Username*"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />

                <input
                    type="email"
                    placeholder="Email*"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />

                <div className="password-field">
                    <input
                        type={showPassword ? 'text' : 'password'}
                        placeholder="Password*"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <img
                        src={showPassword ? hidePasswordIcon : showPasswordIcon}
                        alt="Toggle Password Visibility"
                        className="password-toggle-icon"
                        onClick={togglePasswordVisibility}
                    />
                </div>
                <small>Min 5 alphabets and Min 3 numbers required*</small>
                <PasswordStrengthMeter password={password} />

                <input
                    type={showPassword ? 'text' : 'password'}
                    placeholder="Confirm Password*"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    required
                />

                <select value={userType} onChange={(e) => setUserType(e.target.value)} required>
                    <option value="athlete">Athlete</option>
                    <option value="coach">Coach</option>
                    <option value="admin">Admin</option>
                </select>

                <button type="submit" className="signup-button">Register</button>

                {/* Display success or error messages */}
                {message && <p className="success-message">{message}</p>}
                {error && <p className="error-message">{error}</p>}

                <p>Already have an account? <Link to="/login">Login here</Link></p>
            </form>
        </div>
    );
};

export default SignupPage;
