import React, { useState, useEffect } from 'react';
import './dashboard.css'; // Optional for styling

const dashboard = () => {
    const [athleteCount, setAthleteCount] = useState(0);

    useEffect(() => {
        fetch('http://localhost:8080/admin/dashboard/athletes-count')
            .then((response) => response.json())
            .then((data) => {
                setAthleteCount(data.athleteCount);
            })
            .catch((error) => console.error('Error fetching athlete count:', error));
    }, []);

    return (
        <div className="admin-dashboard">
            <div className="dashboard-header">
                <h1>Admin Dashboard</h1>
            </div>
            <div className="dashboard-content">
                <div className="stat-card">
                    <h2>Total Athletes</h2>
                    <p>{athleteCount}</p>
                </div>
            </div>
        </div>
    );
};

export default dashboard;
