import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from 'react-router-dom';
import SignupPage from './components/signup';
import LoginPage from './components/login';
import HomePage from './components/home';
import EventPage from './components/EventPage';
import ProfilePage from './components/profile';
import Navbar from './components/navbar';
import Footer from './components/footer';

const App = () => {
    const [loggedIn, setLoggedIn] = useState(false);
    const [isAdmin, setIsAdmin] = useState(false);
    const [firstName, setFirstName] = useEffect('');

    // Load login and admin status on mount
    useEffect(() => {
        const isLoggedIn = sessionStorage.getItem("loggedIn") === "true";
        const adminStatus = sessionStorage.getItem("isAdmin") === "true";
        const first_name = sessionStorage.getItem("firstName") || '';
        setLoggedIn(isLoggedIn);
        setIsAdmin(adminStatus);
        setFirstName(first_name);
    }, []);

    const handleLogout = () => {
        setLoggedIn(false);
        setIsAdmin(false);
        setFirstName('');
        sessionStorage.removeItem("loggedIn");
        sessionStorage.removeItem("isAdmin");
        sessionStorage.removeItem("firstName");
    };

    const ProtectedRoute = ({ children }) => {
        return loggedIn ? children : <Navigate to="/login" replace />;
    };

    return (
        <Router>
            <div className="app-container">
                <Navbar 
                    loggedIn={loggedIn} 
                    isAdmin={isAdmin} 
                    onLogout={handleLogout} 
                />
                <div className="main-content">
                    <Routes>
                        {/* Default route */}
                            <Route path="/" element={<Navigate to="/login" replace />} />

                        {/* Public routes */}
                        <Route path="/signup" element={<SignupPage />} />
                        <Route path="/login" element={<LoginPage setLoggedIn={setLoggedIn} />} />

                        {/* Protected routes */}
                        <Route path="/home" element={<ProtectedRoute><HomePage /></ProtectedRoute>} />
                        <Route path="/events" element={<ProtectedRoute><EventPage /></ProtectedRoute>} />
                        <Route path="/" element={<ProtectedRoute><ResultPage /></ProtectedRoute>} />
                        <Route path="/profile" element={<ProtectedRoute><ProfilePage /></ProtectedRoute>} />

                        {/* Admin-only routes */}
                        
                    </Routes>
                </div>
                <footer
                    firstName={firstName}
                    onLogout={handleLogout}
                />
            </div>
        </Router>
    );
};

export default App;
