import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './profile.css';

const ProfilePage = () => {
    const [profileData, setProfileData] = useState({});
    const [isEditing, setIsEditing] = useState(false);
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');

    useEffect(() => {
        const fetchProfileData = async () => {
            const username = sessionStorage.getItem("username");
            const userType = sessionStorage.getItem("userType");

            if (!username || !userType) {
                setError("User not logged in or userType missing.");
                return;
            }

            try {
                const response = await axios.get(`http://localhost:8080/api/profile/${username}`, {
                    params: { userType }
                });
                setProfileData(response.data);
            } catch (error) {
                setError("Failed to load profile details.");
            }
        };

        fetchProfileData();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setProfileData({
            ...profileData,
            [name]: value,
        });
    };

    const handleSave = async () => {
        setMessage('');
        setError('');
        try {
            const username = sessionStorage.getItem("username");
            const userType = sessionStorage.getItem("userType");
            await axios.put(`http://localhost:8080/api/profile/${username}`, {
                ...profileData,
                userType,
            });
            setMessage('Profile updated successfully.');
            setIsEditing(false);
        } catch (error) {
            setError("Failed to save profile details.");
        }
    };

    return (
        <div className="profile-container">
            <h2>User Profile</h2>
            {message && <p className="success-message">{message}</p>}
            {error && <p className="error-message">{error}</p>}
            <div className="profile-details">
                <label>
                    Username:
                    <input
                        type="text"
                        name="username"
                        value={profileData.username || ''}
                        onChange={handleInputChange}
                        disabled
                    />
                </label>
                <label>
                    First Name:
                    <input
                        type="text"
                        name="firstname"
                        value={profileData.firstname || ''}
                        onChange={handleInputChange}
                        disabled={!isEditing}
                    />
                </label>
                <label>
                    Last Name:
                    <input
                        type="text"
                        name="lastname"
                        value={profileData.lastname || ''}
                        onChange={handleInputChange}
                        disabled={!isEditing}
                    />
                </label>
                <label>
                    Email:
                    <input
                        type="email"
                        name="email"
                        value={profileData.email || ''}
                        onChange={handleInputChange}
                        disabled={!isEditing}
                    />
                </label>
                <label>
                    Password:
                    <input
                        type="password"
                        name="password"
                        value={profileData.password || ''}
                        onChange={handleInputChange}
                        disabled={!isEditing}
                    />
                </label>
                <div className="profile-buttons">
                    {isEditing ? (
                        <>
                            <button onClick={handleSave} className="save-button">Save</button>
                            <button onClick={() => setIsEditing(false)} className="cancel-button">Cancel</button>
                        </>
                    ) : (
                        <button onClick={() => setIsEditing(true)} className="edit-button">Edit Profile</button>
                    )}
                </div>
            </div>
        </div>
    );
};

export default ProfilePage;
