import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './news.css';

const NewsPage = () => {
    const [events, setEvents] = useState([]);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchEvents = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/events');
                setEvents(response.data);
            } catch (error) {
                console.error('Error fetching events:', error);
                setError('Failed to fetch events. Please try again later.');
            }
        };

        fetchEvents();
    }, []);

    const isEventRelevant = (eventDate) => {
        const currentDate = new Date();
        const eventDateTime = new Date(eventDate);
        const timeDiff = eventDateTime.getTime() - currentDate.getTime();
        const daysDiff = Math.ceil(timeDiff / (1000 * 3600 * 24));
        return daysDiff > -7 && daysDiff <= 7;
    };

    const getNewsHeading = (event, daysDiff) => {
        if (daysDiff > 0) {
            return `${event.title} upcoming in ${daysDiff} days`;
        } else if (daysDiff === 0) {
            return `${event.title} is today`;
        } else {
            return `${event.title} was ${Math.abs(daysDiff)} days ago`;
        }
    };

    const renderNewsCard = (event) => {
        const currentDate = new Date();
        const eventDateTime = new Date(event.date);
        const timeDiff = eventDateTime.getTime() - currentDate.getTime();
        const daysDiff = Math.ceil(timeDiff / (1000 * 3600 * 24));
        const isCompleted = daysDiff < 0;

        return (
            <div key={event.id} className="news-card">
                <img src={event.image_url} alt={event.title} className="news-image" />
                <div className="news-details">
                    <h3>{getNewsHeading(event, daysDiff)}</h3>
                    <p>Organizer: {event.organizer}</p>
                    <p>Date: {event.date}</p>
                    <p>Time: {event.time}</p>
                    <p>Location: {event.location}</p>
                    <button 
                        onClick={() => navigate(isCompleted ? '/results' : '/events')}
                        className={isCompleted ? "view-result-btn" : "view-event-btn"}
                    >
                        {isCompleted ? "View Result" : "View Event"}
                    </button>
                </div>
            </div>
        );
    };

    const relevantNews = events
        .filter(event => isEventRelevant(event.date))
        .map(event => renderNewsCard(event));

        return (
            <div className="news-container">
                <h2>Event News</h2>
                {error && <p className="error-message">{error}</p>}
                <div className="news-list">
                    {relevantNews}
                </div>
            </div>
        );
};

export default NewsPage;
