import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './home.css';

const HomePage = () => {
    const [events, setEvents] = useState([]);
    const [userRegistrations, setUserRegistrations] = useState([]);
    const [athleteId, setAthleteId] = useState(null);
    const [currentSlide, setCurrentSlide] = useState(0);
    const [error, setError] = useState('');
    const navigate = useNavigate();
    const [firstName, setFirstName] = useState(' ');

    const images = ['cricket.jpg', 'football.jpg', 'basketball.jpg', 'marathon.jpg', 'baseball.jpg'];

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
        }, 2500); // Change image every 3 seconds

        return () => clearInterval(interval);
    }, [images.length]);

    useEffect(() => {
        const loggedIn = sessionStorage.getItem('loggedIn') === 'true';

        if (!loggedIn) {
            navigate('/login');
        } else {
            const fetchAthleteId = async () => {
                const username = sessionStorage.getItem('username');
                if (username) {
                    try {
                        const response = await axios.get(`http://localhost:8080/api/athletes/${username}`);
                        setAthleteId(response.data.id);
                        setFirstName(response.data.firstname);
                    } catch (error) {
                        console.log('Error fetching athlete ID:', error);
                        setError('Failed to fetch athlete ID. Please try again later.');
                    }
                } else {
                    setError('Username not found in session. Please log in again.');
                }
            };

            const fetchEvents = async () => {
                try {
                    const response = await axios.get('http://localhost:8080/api/events');
                    setEvents(response.data);
                } catch (error) {
                    console.log('Error fetching events:', error);
                    setError('Failed to fetch events. Please try again later.');
                }
            };

            fetchAthleteId();
            fetchEvents();
        }
    }, [navigate]);

    useEffect(() => {
        const fetchUserRegistrations = async () => {
            if (athleteId) {
                try {
                    const response = await axios.get(`http://localhost:8080/api/registrations?athleteId=${athleteId}`);
                    setUserRegistrations(Array.isArray(response.data) ? response.data : []);
                } catch (error) {
                    console.error('Error fetching user registrations:', error);
                    setError('Failed to fetch registrations. Please try again later.');
                }
            }
        };

        fetchUserRegistrations();
    }, [athleteId]);

    const isEventUpcoming = (eventDate) => {
        const currentDate = new Date();
        const [day, month, year] = eventDate.split('/').map(Number);
        const eventDateObj = new Date(year, month - 1, day);
        return eventDateObj >= currentDate;
    };

    const upcomingEvents = events
        .filter((event) => isEventUpcoming(event.date) && !userRegistrations.some((reg) => reg.eventId === event.id))
        .slice(0, 3);

    const registeredEvents = userRegistrations.map((registration) => {
        const event = events.find((e) => e.id === registration.eventId);
        return event ? { ...event, registrationStatus: registration.status } : null;
    }).filter(Boolean);

    const recentlyCompletedEvent = registeredEvents
        .filter((event) => !isEventUpcoming(event.date))
        .slice(0, 1);

    const upcomingRegisteredEvents = registeredEvents
        .filter((event) => isEventUpcoming(event.date))
        .slice(0, 2);

    const renderEventCard = (event, isRegistered = false) => (
        <div className="event-card" key={event.id}>
            <img src={event.picture} alt={event.title} className="event-picture" />
            <h3>{event.title}</h3>
            <ul>
                <li><strong>Organizer:</strong> {event.organizer}</li>
                <li><strong>Date:</strong> {event.date}</li>
                <li><strong>Location:</strong> {event.location}</li>
                {isRegistered ? (
                    <li><strong>Status:</strong> <span className={`status-${event.registrationStatus.toLowerCase()}`}>{event.registrationStatus}</span></li>
                ) : (
                    <li><strong>Fees:</strong> {event.fees}</li>
                )}
            </ul>
            <button className="view-event-btn" onClick={() => alert(`View details for event: ${event.title}`)}>View Event</button>
        </div>
    );

    return (
        <div className="home-page">
            <div className="slideshow">
                <img src={images[currentSlide]} alt="Slideshow" className="slideshow-image" />
                <div className="slideshow-overlay">
                    <h1>Hi, {firstName}</h1>
                    <p>Welcome to Athelytics, one-stop destination for sports and events.</p>
                </div>
            </div>
            <div className="upcoming-events">
                <h2>Upcoming Events</h2>
                {upcomingEvents.length > 0 ? (
                    <div className="event-grid">
                        {upcomingEvents.map((event) => renderEventCard(event))}
                    </div>
                ) : (
                    <p>No upcoming events to register.</p>
                )}
            </div>
            <div className="registered-events">
                <h2>Your Registered Events</h2>
                <div className="event-grid">
                    {recentlyCompletedEvent.map((event) => renderEventCard(event, true))}
                    {upcomingRegisteredEvents.map((event) => renderEventCard(event, true))}
                </div>
                {recentlyCompletedEvent.length === 0 && upcomingRegisteredEvents.length === 0 && (
                    <p>No registered events.</p>
                )}
            </div>

            <footer className="footer">
                <div className="footer-section">
                    <h3>Athletyics</h3>
                </div>
                <div className="footer-section">
                    <h3>Links</h3>
                    <ul>
                        <li>Home</li>
                        <li>Events</li>
                        <li>Registrations</li>
                        <li>Results</li>
                        <li>News</li>
                    </ul>
                </div>
                <div className="footer-section">
                    <h3>Account</h3>
                    <ul>
                        <li>Profile</li>
                        <li>Logout</li>
                        <li>Login</li>
                        <li>Signup</li>
                    </ul>
                </div>
            </footer>
        </div>
    );
};

export default HomePage;
