import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // For navigation
import './login.css'; // Import your CSS for styling

const LoginPage = () => {
    // State to hold form data
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        userType: 'athlete' // Default user type
    });

    const [error, setError] = useState('');  // For error message
    const [message, setMessage] = useState('');  // For success message
    const navigate = useNavigate();  // Hook for navigation

    // Handle form input changes
    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setError('');  // Reset error state
            setMessage('');  // Reset message state

            // POST request to the login API
            const response = await axios.post('http://localhost:8080/api/login', formData);

            // Check the response data to navigate based on user type
            if (response.data.includes('/home')) {
                navigate('/home'); // Navigate to home if user is an athlete
            } else if (response.data.includes('/dashboard')) {
                navigate('/dashboard'); // Navigate to dashboard if user is a coach
            } else {
                setMessage(response.data);  // Set the message if backend sends any response
            }
        } catch (error) {
            // Handle error, display the error message
            if (error.response && error.response.data) {
                setError(error.response.data);  // Error from backend
            } else {
                setError('Login failed. Please try again.');  // General error message
            }
        }
    };

    return (
        <div className="login-container">
            <h2>Login</h2>
            <form onSubmit={handleSubmit} className="login-form">
                <div className="form-group">
                    <label>Username:</label>
                    <input
                        type="text"
                        name="username"
                        value={formData.username}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Password:</label>
                    <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>User Type:</label>
                    <select
                        name="userType"
                        value={formData.userType}
                        onChange={handleChange}
                    >
                        <option value="athlete">Athlete</option>
                        <option value="coach">Coach</option>
                    </select>
                </div>
                <button type="submit" className="login-button">Login</button>
                {message && <p className="success-message">{message}</p>}
                {error && <p className="error-message">{error}</p>}
            </form>
        </div>
    );
};

export default LoginPage;
