import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './profile.css';

const ProfilePage = () => {
    const [profileData, setProfileData] = useState({});
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchProfileData = async () => {
            const username = sessionStorage.getItem("username");
            const userType = sessionStorage.getItem("userType");

            if (!username || !userType) {
                setError("User not logged in or userType missing.");
                return;
            }

            try {
                // Fetch profile details with userType as a query parameter
                const response = await axios.get(`http://localhost:8080/api/profile/${username}`, {
                    params: { userType }
                });
                setProfileData(response.data);
            } catch (error) {
                setError("Failed to load profile details.");
            }
        };

        fetchProfileData();
    }, []);

    return (
        <div className="profile-container">
            <h2>User Profile</h2>
            {error ? (
                <p className="error-message">{error}</p>
            ) : (
                <div className="profile-details">
                    <p><strong>Username:</strong> {profileData.username}</p>
                    <p><strong>First Name:</strong> {profileData.firstname}</p>
                    <p><strong>Last Name:</strong> {profileData.lastname}</p>
                    <p><strong>Email:</strong> {profileData.email}</p>
                    <p><strong>Password:</strong> {profileData.password}</p>
                </div>
            )}
        </div>
    );
};

export default ProfilePage;
