import React, { useState } from 'react';
import axios from 'axios';
import './login.css'; // Import the CSS
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection

const LoginPage = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        userType: 'athlete' // Default user type
    });

    const [error, setError] = useState(''); // For error messages
    const [message, setMessage] = useState(''); // For success messages
    const [loggedInUser, setLoggedInUser] = useState(''); // For storing the logged-in username

    // Handle input changes
    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Clear previous messages
            setError('');
            setMessage('');

            // Make a POST request to the backend login endpoint
            const response = await axios.post('http://localhost:8080/api/login', formData);

            // Handle success: display the message, store username, and redirect
            setMessage(response.data.message);
            setLoggedInUser(formData.username); // Store the username
            if (response.data.redirectUrl) {
                navigate(response.data.redirectUrl); // Redirect to the URL
            }
        } catch (error) {
            // Handle error
            if (error.response && error.response.data) {
                setError(error.response.data.error || 'Login failed. Please try again.');
            } else {
                setError('Login failed. Please try again.');
            }
        }
    };

    return (
        <div className="login-container">
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} required />
                </div>
                <div>
                    <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
                </div>
                <div>
                    <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
                </div>
                <div>
                    <select name="userType" value={formData.userType} onChange={handleChange}>
                        <option value="athlete">Athlete</option>
                        <option value="coach">Coach</option>
                    </select>
                </div>
                <button type="submit">Login</button>

                {/* Display success or error message below the button */}
                {message && <p className="success-message">{message}</p>}
                {error && <p className="error-message">{error}</p>}

                {/* Display the logged-in username if available */}
                {loggedInUser && <p className="logged-in-user">Logged in as: {loggedInUser}</p>}
            </form>
        </div>
    );
};

export default LoginPage;
