import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './events.css';

const EventPage = () => {
    const [events, setEvents] = useState([]);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const loggedIn = sessionStorage.getItem('loggedIn') === 'true';

        if (!loggedIn) {
            navigate('/login');
        } else {
            const fetchEvents = async () => {
                try {
                    const response = await axios.get('http://localhost:8080/api/events');
                    setEvents(response.data);
                } catch (error) {
                    setError('Failed to fetch events. Please try again later.');
                }
            };
            fetchEvents();
        }
    }, [navigate]);

    const handleRegister = async (eventId) => {
        const confirmed = window.confirm("Are you sure you want to register for this event?");
        if (confirmed) {
            try {
                const athleteId = sessionStorage.getItem('athleteId'); // Assuming you stored athlete ID in sessionStorage
                const response = await axios.post('http://localhost:8080/api/registrations', { eventId, athleteId });
                alert(response.data); // Show success message
            } catch (error) {
                alert('Registration failed. Please try again.');
            }
        }
    };

    return (
        <div className="event-container">
            <h2>Upcoming Events</h2>
            {error && <p className="error-message">{error}</p>}
            {events.length > 0 ? (
                events.map(event => (
                    <div key={event.id} className="event-box">
                        <img src={event.image_url} alt={event.title} className="event-image"/>
                        <div className="event-details">
                            <h2>Game Type: {event.title}</h2>
                            <h3>Organizer: {event.organizer}</h3>
                            <p>Date: {event.date}</p>
                            <p>Time: {event.time}</p>
                            <p>Fee: RS {event.fee}</p>
                            <p>Location: {event.location}</p>
                            <button className="register-button" onClick={() => handleRegister(event.id)}>Register
                            </button>
                        </div>

                    </div>
                ))
            ) : (
                <p>No events available at the moment.</p>
            )}
        </div>
    );
};

export default EventPage;
