import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './profile.css';

const ProfilePage = () => {
    const [profileData, setProfileData] = useState({ firstname: '', lastname: '', email: '', password: '' });
    const [registrations, setRegistrations] = useState([]);
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');
    const [athleteId, setAthleteId] = useState(null);

    useEffect(() => {
        const fetchProfileData = async () => {
            const username = sessionStorage.getItem("username");
            const userType = sessionStorage.getItem("userType");

            if (!username || !userType) {
                setError("User not logged in or userType missing.");
                return;
            }

            try {
                const profileResponse = await axios.get(`http://localhost:8080/api/profile/${username}`, {
                    params: { userType }
                });
                setProfileData(profileResponse.data);

                if (userType === 'athlete') {
                    const athleteResponse = await axios.get(`http://localhost:8080/api/athletes/${username}`);
                    setAthleteId(athleteResponse.data.id);

                    const registrationsResponse = await axios.get(`http://localhost:8080/api/registrations?athleteId=${athleteId}`);
                    setRegistrations(registrationsResponse.data);
                }
            } catch (error) {
                setError("Failed to load profile details or registrations.");
            }
        };

        fetchProfileData();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setProfileData({
            ...profileData,
            [name]: value
        });
    };

    const handleUpdateProfile = async (e) => {
        e.preventDefault();
        const username = sessionStorage.getItem("username");
        const userType = sessionStorage.getItem("userType");

        if (!username || !userType) {
            setError("User not logged in or userType missing.");
            return;
        }

        try {
            const response = await axios.put(`http://localhost:8080/api/profile/update/${username}`, profileData, {
                params: { userType }
            });

            if (response.status === 200) {
                setMessage("Profile updated successfully.");
            }
        } catch (error) {
            setError("Failed to update profile details.");
        }
    };

    const getStatusColor = (status) => {
        switch (status.toLowerCase()) {
            case 'pending': return 'yellow';
            case 'accepted': return 'green';
            case 'rejected': return 'red';
            default: return 'black';
        }
    };

    return (
        <div className="profile-container">
            <div className='user-profile'>
                <h2>User Profile</h2>
                {error && <p className="error-message">{error}</p>}
                {message && <p className="success-message">{message}</p>}
                {!error && (
                    <form onSubmit={handleUpdateProfile}>
                        <div className="form-group">
                            <label htmlFor="firstname">First Name:</label>
                            <input
                                type="text"
                                id="firstname"
                                name="firstname"
                                value={profileData.firstname}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="lastname">Last Name:</label>
                            <input
                                type="text"
                                id="lastname"
                                name="lastname"
                                value={profileData.lastname}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="email">Email:</label>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                value={profileData.email}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Password:</label>
                            <input
                                type="password"
                                id="password"
                                name="password"
                                value={profileData.password}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <button type="submit" className="btn">Update Profile</button>
                    </form>
                )}
            </div>
            <div className='user-registrations'>
                {registrations.length > 0 && (
                    <div className="user-registrations">
                        <h2>Your Registrations</h2>
                        <table>
                            <thead>
                                <tr>
                                    <th>Title</th>
                                    <th>Organizer</th>
                                    <th>Date</th>
                                    <th>Time</th>
                                    <th>Location</th>
                                    <th>Status</th>
                                </tr>
                            </thead>
                            <tbody>
                                {registrations.map((reg) => (
                                    <tr key={reg.id}>
                                        <td>{reg.event?.title || 'N/A'}</td>
                                        <td>{reg.event?.organizer || 'N/A'}</td>
                                        <td>{reg.event?.date || 'N/A'}</td>
                                        <td>{reg.event?.time || 'N/A'}</td>
                                        <td>{reg.event?.location || 'N/A'}</td>
                                        <td style={{color: getStatusColor(reg.status)}}>{reg.status || 'N/A'}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </div>
        </div>
    );
};

export default ProfilePage;
